select boid, acid, botitle, bocontent, bodate, bonewdate, bohitcount, bcid, boattachdata, bolike, bocommentcount
from board
where boid = #{boId}

mapper.update:
update board set
	boHitCount = boHitCount + 1
	where boId = #{boId}

mapper.select:
select * from boLike
where boid = #{boId}
and acid = #{acId}

mapper.update:
update board set
	boLike = boLike + 1
	where boId = #{boId}

update board set
	boLike = boLike - 1
	where boId = #{boId}

update boLike set
bolState = #{bolState}
where boId = #{boId}
and acId = #{acId}

update board set
	boLike = boLike + 2
	where boId = #{boId}

update board set
	boLike = boLike - 2
	where boId = #{boId}

update board
set botitle = #{boTitle}, bocontent = #{boContent} , boupdatecheck = #{boUpdateCheck}, boattachdata = #{boAttachdata}
where boId = #{boId}

