mapper.select:
select * from bocLike
where bocId = #{bocId}
and acid = #{acId}

mapper.update:
update boardComment set
	bocLike = bocLike + 1
	where bocId = #{bocId}

update bocLike set
boclState = #{boclState}
where bocId = #{bocId}
and acId = #{acId}

update boardComment set
	bocLike = bocLike - 1
	where bocId = #{bocId}

update boardComment set
	bocLike = bocLike + 2
	where bocId = #{bocId}

update boardComment set
	bocLike = bocLike - 2
	where bocId = #{bocId}
