mapper.select:
select prid, prname, pcid, prprice, prinfo, prmemo, primage
from product
where prid=#{prId}



select rnum, prid, prname, pcid, prprice, prinfo, prmemo, primage
   from (
       select rownum as rnum , prid, prname, pcid, prprice, prinfo, prmemo, primage
       from (
          select prid, prname, pcid, prprice, prinfo, prmemo, primage
          from product
          order by prid
       )
       where rownum <= #{endRowNo}
   )
   where rnum >= #{startRowNo}
